    ; (window as any).switchToATexture = switchToATexture
    ; (window as any).switchToBTexture = switchToBTexture
    ; (window as any).getColorName = getColorName
    ; (window as any).getColorForTexture = getColorForTexture
    ; (window as any).completeCustomization = completeCustomization
    ; (window as any).dumpMeshNames = (limit?: number) => logMeshNames(limit)


  // 若进入页面时弹窗已显示（极少见），也补一次
  if (showConfirm.value) applyNoiseToConfirmCard()

})

// 监听路由变化，实时切换 A/B 纹理
watch(
  () => route.fullPath,
  () => {
    const nextA = resolveAreaParam('a', aParam.value)
    const nextB = resolveAreaParam('b', bParam.value)
    aParam.value = nextA
    bParam.value = nextB
    if (shoeModel) {
      applyTextureToMeshA(getTexturePath('A', aParam.value))
      applyTextureToMeshB(getTexturePath('B', bParam.value))
    }
    // 同步 UI 选中状态（若命中我们列表）
    if (aTextureNames.includes(nextA)) selectedATexture.value = nextA
    if (bTextureNames.includes(nextB)) selectedBTexture.value = nextB
  }
)

onUnmounted(() => {
  if (animationId) {
    cancelAnimationFrame(animationId)
  }
  if (renderer) {
    renderer.dispose()
  }
  if (controls) {
    controls.dispose()
  }

})
</script>

